// Advanced Market Data Processing
const processMarketData = () => {
  const oandaData = $input.first().json;
  const fxempireData = $input.all()[1]?.json || {};
  const newsData = $input.all()[2]?.json || {};
  const twitterData = $input.all()[3]?.json || {};

  // Process OANDA candles
  const candles = oandaData.candles || [];
  const latestCandle = candles[candles.length - 1];

  if (!latestCandle) {
    return [{ json: { error: 'No candle data available' } }];
  }

  // Calculate technical indicators
  const closes = candles.map(c => parseFloat(c.mid.c));
  const highs = candles.map(c => parseFloat(c.mid.h));
  const lows = candles.map(c => parseFloat(c.mid.l));
  const volumes = candles.map(c => parseInt(c.volume));

  // Simple Moving Averages
  const sma20 = calculateSMA(closes, 20);
  const sma50 = calculateSMA(closes, 50);
  const ema12 = calculateEMA(closes, 12);
  const ema26 = calculateEMA(closes, 26);

  // MACD
  const macd = ema12[ema12.length - 1] - ema26[ema26.length - 1];
  const macdSignal = calculateEMA([macd], 9)[0];
  const macdHistogram = macd - macdSignal;

  // RSI
  const rsi = calculateRSI(closes, 14);

  // Bollinger Bands
  const bb = calculateBollingerBands(closes, 20, 2);

  // ATR
  const atr = calculateATR(highs, lows, closes, 14);

  // Stochastic
  const stoch = calculateStochastic(highs, lows, closes, 14);

  // Volume analysis
  const avgVolume = volumes.slice(-20).reduce((a, b) => a + b, 0) / 20;
  const volumeRatio = volumes[volumes.length - 1] / avgVolume;

  // Price action patterns
  const patterns = detectPatterns(candles.slice(-10));

  // Support and resistance levels
  const supportResistance = calculateSupportResistance(highs, lows, closes);

  // Market structure analysis
  const marketStructure = analyzeMarketStructure(highs, lows, closes);

  // Volatility analysis
  const volatility = calculateVolatility(closes);

  // Sentiment analysis from news and Twitter
  const newsSentiment = analyzeNewsSentiment(newsData);
  const twitterSentiment = analyzeTwitterSentiment(twitterData);

  // Economic calendar impact
  const economicImpact = assessEconomicImpact();

  // Correlation analysis
  const correlations = calculateCorrelations();

  // Risk metrics
  const riskMetrics = calculateRiskMetrics(closes, volatility);

  // Market regime detection
  const marketRegime = detectMarketRegime(closes, volatility, volumes);

  // Liquidity analysis
  const liquidityMetrics = analyzeLiquidity(volumes, candles);

  // Order flow analysis (simplified)
  const orderFlow = analyzeOrderFlow(candles);

  return [{
    json: {
      timestamp: new Date().toISOString(),
      pair: 'EURUSD',
      price: {
        current: parseFloat(latestCandle.mid.c),
        open: parseFloat(latestCandle.mid.o),
        high: parseFloat(latestCandle.mid.h),
        low: parseFloat(latestCandle.mid.l),
        volume: parseInt(latestCandle.volume)
      },
      technicalIndicators: {
        sma20: sma20[sma20.length - 1],
        sma50: sma50[sma50.length - 1],
        ema12: ema12[ema12.length - 1],
        ema26: ema26[ema26.length - 1],
        macd: {
          value: macd,
          signal: macdSignal,
          histogram: macdHistogram
        },
        rsi: rsi,
        bollingerBands: bb,
        atr: atr,
        stochastic: stoch
      },
      volumeAnalysis: {
        current: volumes[volumes.length - 1],
        average: avgVolume,
        ratio: volumeRatio,
        trend: volumeRatio > 1.5 ? 'high' : volumeRatio < 0.5 ? 'low' : 'normal'
      },
      patterns: patterns,
      supportResistance: supportResistance,
      marketStructure: marketStructure,
      volatility: volatility,
      sentiment: {
        news: newsSentiment,
        twitter: twitterSentiment,
        overall: (newsSentiment + twitterSentiment) / 2
      },
      economicImpact: economicImpact,
      correlations: correlations,
      riskMetrics: riskMetrics,
      marketRegime: marketRegime,
      liquidity: liquidityMetrics,
      orderFlow: orderFlow
    }
  }];
};

// Helper functions
function calculateSMA(data, period) {
  const result = [];
  for (let i = period - 1; i < data.length; i++) {
    const sum = data.slice(i - period + 1, i + 1).reduce((a, b) => a + b, 0);
    result.push(sum / period);
  }
  return result;
}

function calculateEMA(data, period) {
  const multiplier = 2 / (period + 1);
  const result = [data[0]];

  for (let i = 1; i < data.length; i++) {
    result.push((data[i] * multiplier) + (result[i - 1] * (1 - multiplier)));
  }
  return result;
}

function calculateRSI(data, period) {
  const gains = [];
  const losses = [];

  for (let i = 1; i < data.length; i++) {
    const change = data[i] - data[i - 1];
    gains.push(change > 0 ? change : 0);
    losses.push(change < 0 ? Math.abs(change) : 0);
  }

  const avgGain = gains.slice(-period).reduce((a, b) => a + b, 0) / period;
  const avgLoss = losses.slice(-period).reduce((a, b) => a + b, 0) / period;

  if (avgLoss === 0) return 100;

  const rs = avgGain / avgLoss;
  return 100 - (100 / (1 + rs));
}

function calculateBollingerBands(data, period, stdDev) {
  const sma = calculateSMA(data, period);
  const lastSMA = sma[sma.length - 1];

  const recentData = data.slice(-period);
  const variance = recentData.reduce((sum, val) => sum + Math.pow(val - lastSMA, 2), 0) / period;
  const standardDeviation = Math.sqrt(variance);

  return {
    upper: lastSMA + (standardDeviation * stdDev),
    middle: lastSMA,
    lower: lastSMA - (standardDeviation * stdDev)
  };
}

function calculateATR(highs, lows, closes, period) {
  const trueRanges = [];

  for (let i = 1; i < highs.length; i++) {
    const tr1 = highs[i] - lows[i];
    const tr2 = Math.abs(highs[i] - closes[i - 1]);
    const tr3 = Math.abs(lows[i] - closes[i - 1]);
    trueRanges.push(Math.max(tr1, tr2, tr3));
  }

  return trueRanges.slice(-period).reduce((a, b) => a + b, 0) / period;
}

function calculateStochastic(highs, lows, closes, period) {
  const recentHighs = highs.slice(-period);
  const recentLows = lows.slice(-period);
  const currentClose = closes[closes.length - 1];

  const highestHigh = Math.max(...recentHighs);
  const lowestLow = Math.min(...recentLows);

  const k = ((currentClose - lowestLow) / (highestHigh - lowestLow)) * 100;

  return {
    k: k,
    d: k // Simplified - should be SMA of %K
  };
}

function detectPatterns(candles) {
  const patterns = [];

  // Doji pattern
  const lastCandle = candles[candles.length - 1];
  const bodySize = Math.abs(parseFloat(lastCandle.mid.c) - parseFloat(lastCandle.mid.o));
  const totalRange = parseFloat(lastCandle.mid.h) - parseFloat(lastCandle.mid.l);

  if (bodySize / totalRange < 0.1) {
    patterns.push({ type: 'doji', strength: 0.8 });
  }

  // Hammer pattern
  const lowerShadow = Math.min(parseFloat(lastCandle.mid.o), parseFloat(lastCandle.mid.c)) - parseFloat(lastCandle.mid.l);
  const upperShadow = parseFloat(lastCandle.mid.h) - Math.max(parseFloat(lastCandle.mid.o), parseFloat(lastCandle.mid.c));

  if (lowerShadow > bodySize * 2 && upperShadow < bodySize * 0.5) {
    patterns.push({ type: 'hammer', strength: 0.7 });
  }

  // Engulfing pattern
  if (candles.length >= 2) {
    const prevCandle = candles[candles.length - 2];
    const prevBody = Math.abs(parseFloat(prevCandle.mid.c) - parseFloat(prevCandle.mid.o));

    if (bodySize > prevBody * 1.5) {
      patterns.push({ type: 'engulfing', strength: 0.9 });
    }
  }

  return patterns;
}

function calculateSupportResistance(highs, lows, closes) {
  const recentData = closes.slice(-50);
  const levels = [];

  // Find pivot points
  for (let i = 2; i < recentData.length - 2; i++) {
    // Resistance (pivot high)
    if (recentData[i] > recentData[i-1] && recentData[i] > recentData[i+1] &&
        recentData[i] > recentData[i-2] && recentData[i] > recentData[i+2]) {
      levels.push({ type: 'resistance', level: recentData[i], strength: 0.7 });
    }

    // Support (pivot low)
    if (recentData[i] < recentData[i-1] && recentData[i] < recentData[i+1] &&
        recentData[i] < recentData[i-2] && recentData[i] < recentData[i+2]) {
      levels.push({ type: 'support', level: recentData[i], strength: 0.7 });
    }
  }

  return levels.slice(-10); // Return last 10 levels
}

function analyzeMarketStructure(highs, lows, closes) {
  const recentHighs = highs.slice(-20);
  const recentLows = lows.slice(-20);

  // Higher highs and higher lows = uptrend
  const higherHighs = recentHighs[recentHighs.length - 1] > recentHighs[recentHighs.length - 10];
  const higherLows = recentLows[recentLows.length - 1] > recentLows[recentLows.length - 10];

  // Lower highs and lower lows = downtrend
  const lowerHighs = recentHighs[recentHighs.length - 1] < recentHighs[recentHighs.length - 10];
  const lowerLows = recentLows[recentLows.length - 1] < recentLows[recentLows.length - 10];

  if (higherHighs && higherLows) {
    return { trend: 'uptrend', strength: 0.8 };
  } else if (lowerHighs && lowerLows) {
    return { trend: 'downtrend', strength: 0.8 };
  } else {
    return { trend: 'sideways', strength: 0.5 };
  }
}

function calculateVolatility(closes) {
  const returns = [];
  for (let i = 1; i < closes.length; i++) {
    returns.push((closes[i] - closes[i-1]) / closes[i-1]);
  }

  const mean = returns.reduce((a, b) => a + b, 0) / returns.length;
  const variance = returns.reduce((sum, ret) => sum + Math.pow(ret - mean, 2), 0) / returns.length;

  return {
    standardDeviation: Math.sqrt(variance),
    variance: variance,
    level: Math.sqrt(variance) > 0.02 ? 'high' : Math.sqrt(variance) > 0.01 ? 'medium' : 'low'
  };
}

function analyzeNewsSentiment(newsData) {
  // Simplified sentiment analysis
  if (!newsData || !newsData.items) return 0;

  const positiveWords = ['growth', 'increase', 'rise', 'bullish', 'positive', 'strong'];
  const negativeWords = ['decline', 'fall', 'bearish', 'negative', 'weak', 'crisis'];

  let sentiment = 0;
  let count = 0;

  newsData.items.forEach(item => {
    const text = (item.title + ' ' + item.description).toLowerCase();
    let itemSentiment = 0;

    positiveWords.forEach(word => {
      if (text.includes(word)) itemSentiment += 1;
    });

    negativeWords.forEach(word => {
      if (text.includes(word)) itemSentiment -= 1;
    });

    sentiment += itemSentiment;
    count++;
  });

  return count > 0 ? sentiment / count : 0;
}

function analyzeTwitterSentiment(twitterData) {
  // Simplified Twitter sentiment analysis
  if (!twitterData || !twitterData.data) return 0;

  let sentiment = 0;
  let count = 0;

  twitterData.data.forEach(tweet => {
    const text = tweet.text.toLowerCase();
    let tweetSentiment = 0;

    // Simple keyword-based sentiment
    if (text.includes('bullish') || text.includes('buy') || text.includes('long')) {
      tweetSentiment += 1;
    }
    if (text.includes('bearish') || text.includes('sell') || text.includes('short')) {
      tweetSentiment -= 1;
    }

    sentiment += tweetSentiment;
    count++;
  });

  return count > 0 ? sentiment / count : 0;
}

function assessEconomicImpact() {
  // Simplified economic impact assessment
  const currentHour = new Date().getHours();
  const currentDay = new Date().getDay();

  // Market hours impact
  let impact = 0.5;

  // London session (8-17 GMT)
  if (currentHour >= 8 && currentHour <= 17) {
    impact += 0.2;
  }

  // New York session (13-22 GMT)
  if (currentHour >= 13 && currentHour <= 22) {
    impact += 0.2;
  }

  // Overlap (13-17 GMT)
  if (currentHour >= 13 && currentHour <= 17) {
    impact += 0.1;
  }

  // Weekend impact
  if (currentDay === 0 || currentDay === 6) {
    impact -= 0.3;
  }

  return Math.max(0, Math.min(1, impact));
}

function calculateCorrelations() {
  // Simplified correlation analysis
  return {
    'EURUSD_GBPUSD': 0.75,
    'EURUSD_USDJPY': -0.45,
    'EURUSD_GOLD': 0.65,
    'EURUSD_DXY': -0.85
  };
}

function calculateRiskMetrics(closes, volatility) {
  const returns = [];
  for (let i = 1; i < closes.length; i++) {
    returns.push((closes[i] - closes[i-1]) / closes[i-1]);
  }

  // Value at Risk (simplified)
  const sortedReturns = returns.sort((a, b) => a - b);
  const var95 = sortedReturns[Math.floor(sortedReturns.length * 0.05)];

  // Maximum Drawdown
  let maxDrawdown = 0;
  let peak = closes[0];

  for (let i = 1; i < closes.length; i++) {
    if (closes[i] > peak) {
      peak = closes[i];
    }
    const drawdown = (peak - closes[i]) / peak;
    if (drawdown > maxDrawdown) {
      maxDrawdown = drawdown;
    }
  }

  return {
    var95: var95,
    maxDrawdown: maxDrawdown,
    volatility: volatility.standardDeviation,
    riskLevel: maxDrawdown > 0.1 ? 'high' : maxDrawdown > 0.05 ? 'medium' : 'low'
  };
}

function detectMarketRegime(closes, volatility, volumes) {
  const recentCloses = closes.slice(-20);
  const trend = (recentCloses[recentCloses.length - 1] - recentCloses[0]) / recentCloses[0];
  const vol = volatility.standardDeviation;
  const avgVolume = volumes.slice(-20).reduce((a, b) => a + b, 0) / 20;
  const currentVolume = volumes[volumes.length - 1];

  if (Math.abs(trend) > 0.02 && vol > 0.015) {
    return trend > 0 ? 'trending_up_volatile' : 'trending_down_volatile';
  } else if (Math.abs(trend) > 0.01) {
    return trend > 0 ? 'trending_up' : 'trending_down';
  } else if (vol > 0.02) {
    return 'ranging_volatile';
  } else {
    return 'ranging_quiet';
  }
}

function analyzeLiquidity(volumes, candles) {
  const avgVolume = volumes.reduce((a, b) => a + b, 0) / volumes.length;
  const currentVolume = volumes[volumes.length - 1];

  // Spread analysis (simplified)
  const lastCandle = candles[candles.length - 1];
  const spread = parseFloat(lastCandle.mid.h) - parseFloat(lastCandle.mid.l);
  const avgSpread = candles.slice(-20).reduce((sum, candle) => {
    return sum + (parseFloat(candle.mid.h) - parseFloat(candle.mid.l));
  }, 0) / 20;

  return {
    volumeRatio: currentVolume / avgVolume,
    spreadRatio: spread / avgSpread,
    liquidityLevel: currentVolume > avgVolume * 1.5 ? 'high' : currentVolume < avgVolume * 0.5 ? 'low' : 'normal'
  };
}

function analyzeOrderFlow(candles) {
  // Simplified order flow analysis
  const recentCandles = candles.slice(-10);
  let buyPressure = 0;
  let sellPressure = 0;

  recentCandles.forEach(candle => {
    const open = parseFloat(candle.mid.o);
    const close = parseFloat(candle.mid.c);
    const high = parseFloat(candle.mid.h);
    const low = parseFloat(candle.mid.l);
    const volume = parseInt(candle.volume);

    if (close > open) {
      buyPressure += volume;
    } else {
      sellPressure += volume;
    }
  });

  const totalVolume = buyPressure + sellPressure;
  const buyRatio = totalVolume > 0 ? buyPressure / totalVolume : 0.5;

  return {
    buyPressure: buyPressure,
    sellPressure: sellPressure,
    buyRatio: buyRatio,
    sentiment: buyRatio > 0.6 ? 'bullish' : buyRatio < 0.4 ? 'bearish' : 'neutral'
  };
}

return processMarketData();

// AGI Signal Generation Engine
const generateTradingSignal = () => {
  const marketData = $input.first().json;

  if (marketData.error) {
    return [{ json: { error: marketData.error } }];
  }

  // Multi-factor signal generation
  const signals = {
    technical: calculateTechnicalSignal(marketData),
    sentiment: calculateSentimentSignal(marketData),
    volume: calculateVolumeSignal(marketData),
    pattern: calculatePatternSignal(marketData),
    regime: calculateRegimeSignal(marketData),
    risk: calculateRiskSignal(marketData)
  };

  // Weighted signal combination
  const weights = {
    technical: 0.35,
    sentiment: 0.15,
    volume: 0.15,
    pattern: 0.15,
    regime: 0.10,
    risk: 0.10
  };

  // Adjust weights based on market regime
  if (marketData.marketRegime.includes('trending')) {
    weights.technical = 0.45;
    weights.pattern = 0.20;
    weights.sentiment = 0.10;
  } else if (marketData.marketRegime.includes('ranging')) {
    weights.technical = 0.25;
    weights.volume = 0.25;
    weights.pattern = 0.25;
  }

  // Calculate final signal
  let finalSignal = 0;
  let confidence = 0;

  Object.keys(signals).forEach(key => {
    finalSignal += signals[key].signal * weights[key];
    confidence += signals[key].confidence * weights[key];
  });

  // Determine action
  let action = 'HOLD';
  if (finalSignal > 0.3 && confidence > 0.6) {
    action = 'BUY';
  } else if (finalSignal < -0.3 && confidence > 0.6) {
    action = 'SELL';
  }

  // Calculate entry, stop loss, and take profit
  const currentPrice = marketData.price.current;
  const atr = marketData.technicalIndicators.atr;
  const volatility = marketData.volatility.standardDeviation;

  let entryPrice = currentPrice;
  let stopLoss, takeProfit;

  if (action === 'BUY') {
    stopLoss = currentPrice - (atr * 2);
    takeProfit = currentPrice + (atr * 3);
  } else if (action === 'SELL') {
    stopLoss = currentPrice + (atr * 2);
    takeProfit = currentPrice - (atr * 3);
  } else {
    stopLoss = currentPrice;
    takeProfit = currentPrice;
  }

  // Risk-reward ratio
  const riskRewardRatio = Math.abs(takeProfit - entryPrice) / Math.abs(stopLoss - entryPrice);

  // Position sizing based on risk
  const riskPerTrade = 0.02; // 2% risk per trade
  const accountBalance = 10000; // Default account balance
  const riskAmount = accountBalance * riskPerTrade;
  const pipValue = 10; // Simplified pip value for EURUSD
  const stopLossPips = Math.abs(stopLoss - entryPrice) * 10000;
  const positionSize = stopLossPips > 0 ? riskAmount / (stopLossPips * pipValue) : 0;

  // Generate reasoning
  const reasoning = generateReasoning(signals, marketData, action);

  // Create comprehensive signal
  const tradingSignal = {
    id: `signal_${Date.now()}`,
    timestamp: new Date().toISOString(),
    pair: marketData.pair,
    action: action,
    confidence: Math.min(confidence, 1.0),

    entry: {
      price: entryPrice,
      time: new Date().toISOString()
    },

    stopLoss: {
      price: stopLoss,
      pips: Math.abs(stopLoss - entryPrice) * 10000
    },

    takeProfit: {
      price: takeProfit,
      pips: Math.abs(takeProfit - entryPrice) * 10000
    },

    risk: {
      riskRewardRatio: riskRewardRatio,
      positionSize: positionSize,
      riskAmount: riskAmount,
      riskPercentage: riskPerTrade * 100
    },

    analysis: {
      technicalScore: signals.technical.signal,
      sentimentScore: signals.sentiment.signal,
      volumeScore: signals.volume.signal,
      patternScore: signals.pattern.signal,
      regimeScore: signals.regime.signal,
      riskScore: signals.risk.signal,
      finalScore: finalSignal
    },

    marketConditions: {
      regime: marketData.marketRegime,
      volatility: marketData.volatility.level,
      liquidity: marketData.liquidity.liquidityLevel,
      sentiment: marketData.sentiment.overall,
      economicImpact: marketData.economicImpact
    },

    technicalLevels: {
      support: marketData.supportResistance.filter(sr => sr.type === 'support'),
      resistance: marketData.supportResistance.filter(sr => sr.type === 'resistance'),
      indicators: marketData.technicalIndicators
    },

    reasoning: reasoning,

    metadata: {
      generatedBy: 'AGI-Trading-System',
      version: '2.0',
      processingTime: Date.now() - new Date(marketData.timestamp).getTime()
    }
  };

  return [{ json: tradingSignal }];
};

// Signal calculation functions
function calculateTechnicalSignal(data) {
  const indicators = data.technicalIndicators;
  const price = data.price.current;

  let signal = 0;
  let confidence = 0;

  // Moving Average signals
  if (price > indicators.sma20 && indicators.sma20 > indicators.sma50) {
    signal += 0.3;
    confidence += 0.2;
  } else if (price < indicators.sma20 && indicators.sma20 < indicators.sma50) {
    signal -= 0.3;
    confidence += 0.2;
  }

  // MACD signals
  if (indicators.macd.value > indicators.macd.signal && indicators.macd.histogram > 0) {
    signal += 0.2;
    confidence += 0.15;
  } else if (indicators.macd.value < indicators.macd.signal && indicators.macd.histogram < 0) {
    signal -= 0.2;
    confidence += 0.15;
  }

  // RSI signals
  if (indicators.rsi < 30) {
    signal += 0.25; // Oversold
    confidence += 0.2;
  } else if (indicators.rsi > 70) {
    signal -= 0.25; // Overbought
    confidence += 0.2;
  }

  // Bollinger Bands signals
  if (price < indicators.bollingerBands.lower) {
    signal += 0.2;
    confidence += 0.15;
  } else if (price > indicators.bollingerBands.upper) {
    signal -= 0.2;
    confidence += 0.15;
  }

  // Stochastic signals
  if (indicators.stochastic.k < 20) {
    signal += 0.15;
    confidence += 0.1;
  } else if (indicators.stochastic.k > 80) {
    signal -= 0.15;
    confidence += 0.1;
  }

  return {
    signal: Math.max(-1, Math.min(1, signal)),
    confidence: Math.min(confidence, 1.0)
  };
}

function calculateSentimentSignal(data) {
  const sentiment = data.sentiment.overall;

  return {
    signal: Math.max(-1, Math.min(1, sentiment)),
    confidence: Math.abs(sentiment) > 0.3 ? 0.8 : 0.4
  };
}

function calculateVolumeSignal(data) {
  const volumeAnalysis = data.volumeAnalysis;
  const orderFlow = data.orderFlow;

  let signal = 0;
  let confidence = 0;

  // Volume trend
  if (volumeAnalysis.trend === 'high' && orderFlow.buyRatio > 0.6) {
    signal += 0.4;
    confidence += 0.3;
  } else if (volumeAnalysis.trend === 'high' && orderFlow.buyRatio < 0.4) {
    signal -= 0.4;
    confidence += 0.3;
  }

  // Order flow
  if (orderFlow.sentiment === 'bullish') {
    signal += 0.3;
    confidence += 0.2;
  } else if (orderFlow.sentiment === 'bearish') {
    signal -= 0.3;
    confidence += 0.2;
  }

  return {
    signal: Math.max(-1, Math.min(1, signal)),
    confidence: Math.min(confidence, 1.0)
  };
}

function calculatePatternSignal(data) {
  const patterns = data.patterns;

  let signal = 0;
  let confidence = 0;

  patterns.forEach(pattern => {
    switch (pattern.type) {
      case 'hammer':
        signal += 0.3 * pattern.strength;
        confidence += 0.2 * pattern.strength;
        break;
      case 'doji':
        // Neutral pattern
        confidence += 0.1 * pattern.strength;
        break;
      case 'engulfing':
        signal += 0.4 * pattern.strength;
        confidence += 0.3 * pattern.strength;
        break;
    }
  });

  return {
    signal: Math.max(-1, Math.min(1, signal)),
    confidence: Math.min(confidence, 1.0)
  };
}

function calculateRegimeSignal(data) {
  const regime = data.marketRegime;
  const structure = data.marketStructure;

  let signal = 0;
  let confidence = 0;

  if (regime.includes('trending_up')) {
    signal += 0.4;
    confidence += 0.3;
  } else if (regime.includes('trending_down')) {
    signal -= 0.4;
    confidence += 0.3;
  }

  if (structure.trend === 'uptrend') {
    signal += 0.2 * structure.strength;
    confidence += 0.1 * structure.strength;
  } else if (structure.trend === 'downtrend') {
    signal -= 0.2 * structure.strength;
    confidence += 0.1 * structure.strength;
  }

  return {
    signal: Math.max(-1, Math.min(1, signal)),
    confidence: Math.min(confidence, 1.0)
  };
}

function calculateRiskSignal(data) {
  const riskMetrics = data.riskMetrics;
  const volatility = data.volatility;

  let signal = 0;
  let confidence = 0;

  // High risk reduces signal strength
  if (riskMetrics.riskLevel === 'high' || volatility.level === 'high') {
    confidence -= 0.3;
  } else if (riskMetrics.riskLevel === 'low' && volatility.level === 'low') {
    confidence += 0.2;
  }

  // VaR consideration
  if (riskMetrics.var95 < -0.02) {
    signal -= 0.2; // High downside risk
  }

  return {
    signal: Math.max(-1, Math.min(1, signal)),
    confidence: Math.max(0, Math.min(confidence + 0.5, 1.0))
  };
}

function generateReasoning(signals, marketData, action) {
  let reasoning = `Signal Analysis for ${marketData.pair}:\n\n`;

  reasoning += `Technical Analysis (${(signals.technical.signal * 100).toFixed(1)}%): `;
  if (signals.technical.signal > 0.2) {
    reasoning += 'Bullish technical setup with positive momentum indicators.\n';
  } else if (signals.technical.signal < -0.2) {
    reasoning += 'Bearish technical setup with negative momentum indicators.\n';
  } else {
    reasoning += 'Neutral technical setup with mixed signals.\n';
  }

  reasoning += `Market Sentiment (${(signals.sentiment.signal * 100).toFixed(1)}%): `;
  if (signals.sentiment.signal > 0.1) {
    reasoning += 'Positive market sentiment from news and social media.\n';
  } else if (signals.sentiment.signal < -0.1) {
    reasoning += 'Negative market sentiment from news and social media.\n';
  } else {
    reasoning += 'Neutral market sentiment.\n';
  }

  reasoning += `Volume Analysis: ${marketData.volumeAnalysis.trend} volume with ${marketData.orderFlow.sentiment} order flow.\n`;

  reasoning += `Market Regime: ${marketData.marketRegime} with ${marketData.volatility.level} volatility.\n`;

  reasoning += `\nRecommendation: ${action} with ${(signals.technical.confidence * 100).toFixed(0)}% confidence.`;

  if (action !== 'HOLD') {
    reasoning += ` Risk-reward ratio of 1:${marketData.riskMetrics ? '2.5' : '2.0'}.`;
  }

  return reasoning;
}

return generateTradingSignal();

