      maxCorrelatedExposure: 0.3, // 30% max correlated exposure
      riskBudget: 0.02, // 2% risk per trade
      applied: true,
      timestamp: new Date().toISOString()
    }
  };

  // Final risk check
  if (riskAdjustedSignal.risk.overallRiskScore > 0.8) {
    riskAdjustedSignal.action = 'HOLD';
    riskAdjustedSignal.reasoning += '\n\nRisk Management Override: Signal rejected due to high overall risk score.';
  }

  return [{ json: riskAdjustedSignal }];
};

function assessPortfolioRisk() {
  // Simplified portfolio risk assessment
  // In production, this would connect to actual portfolio data
  const openPositions = 3; // Example
  const portfolioValue = 10000; // Example
  const currentDrawdown = 0.05; // 5% current drawdown

  let riskScore = 0;

  // Too many open positions
  if (openPositions > 5) riskScore += 0.3;

  // High current drawdown
  if (currentDrawdown > 0.1) riskScore += 0.4;

  // Portfolio concentration
  riskScore += Math.min(openPositions / 10, 0.3);

  return Math.min(riskScore, 1.0);
}

function assessCorrelationRisk(pair) {
  // Simplified correlation risk assessment
  const correlatedPairs = {
    'EURUSD': ['GBPUSD', 'AUDUSD', 'NZDUSD'],
    'GBPUSD': ['EURUSD', 'EURGBP'],
    'USDJPY': ['USDCHF', 'USDCAD']
  };

  const currentPairCorrelations = correlatedPairs[pair] || [];
  const exposureCount = currentPairCorrelations.length; // Simplified

  return Math.min(exposureCount / 5, 1.0);
}

function assessMarketRisk(marketConditions) {
  let riskScore = 0;

  // High volatility increases risk
  if (marketConditions.volatility === 'high') {
    riskScore += 0.4;
  }

  // Low liquidity increases risk
  if (marketConditions.liquidity === 'low') {
    riskScore += 0.3;
  }

  // Economic events increase risk
  if (marketConditions.economicImpact > 0.8) {
    riskScore += 0.3;
  }

  return Math.min(riskScore, 1.0);
}

function calculateKellyFraction(signal) {
  // Simplified Kelly Criterion calculation
  const winRate = 0.6; // 60% historical win rate
  const avgWin = 150; // Average win in pips
  const avgLoss = 100; // Average loss in pips

  const b = avgWin / avgLoss; // Odds
  const p = winRate; // Probability of win
  const q = 1 - p; // Probability of loss

  const kellyFraction = (b * p - q) / b;

  // Cap Kelly fraction to prevent over-leveraging
  return Math.max(0, Math.min(kellyFraction, 0.25));
}

function calculateDynamicStopLoss(signal) {
  const currentPrice = signal.entry.price;
  const originalStopLoss = signal.stopLoss.price;
  const atr = signal.technicalLevels.indicators.atr;
  const volatility = signal.marketConditions.volatility;

  let multiplier = 2.0; // Base ATR multiplier

  // Adjust based on volatility
  if (volatility === 'high') {
    multiplier = 2.5;
  } else if (volatility === 'low') {
    multiplier = 1.5;
  }

  // Adjust based on market regime
  if (signal.marketConditions.regime.includes('volatile')) {
    multiplier += 0.5;
  }

  let dynamicStopLoss;
  if (signal.action === 'BUY') {
    dynamicStopLoss = currentPrice - (atr * multiplier);
  } else {
    dynamicStopLoss = currentPrice + (atr * multiplier);
  }

  // Ensure dynamic stop loss is not worse than original
  if (signal.action === 'BUY') {
    return Math.max(dynamicStopLoss, originalStopLoss);
  } else {
    return Math.min(dynamicStopLoss, originalStopLoss);
  }
}

return applyRiskManagement();

