// Performance Tracking and Learning System
const trackPerformance = () => {
  const signal = $input.first().json;

  // Store signal for future performance evaluation
  const performanceData = {
    signalId: signal.id,
    pair: signal.pair,
    action: signal.action,
    confidence: signal.confidence,
    entryPrice: signal.entry.price,
    stopLoss: signal.stopLoss.price,
    takeProfit: signal.takeProfit.price,
    positionSize: signal.risk.adjustedPositionSize,
    marketConditions: signal.marketConditions,
    technicalScores: signal.analysis,
    timestamp: signal.timestamp,
    status: 'active'
  };

  // Check for closed positions and calculate performance
  const closedPositions = checkClosedPositions();

  // Update model performance metrics
  const performanceMetrics = calculatePerformanceMetrics(closedPositions);

  // Trigger model retraining if performance degrades
  const retrainingNeeded = checkRetrainingCriteria(performanceMetrics);

  return [{
    json: {
      ...performanceData,
      performanceMetrics: performanceMetrics,
      retrainingNeeded: retrainingNeeded,
      closedPositionsCount: closedPositions.length
    }
  }];
};

function checkClosedPositions() {
  // Simplified - in production, this would check actual broker positions
  return [
    { id: 'pos1', result: 'win', pips: 50, roi: 2.5 },
    { id: 'pos2', result: 'loss', pips: -30, roi: -1.5 },
    { id: 'pos3', result: 'win', pips: 75, roi: 3.2 }
  ];
}

function calculatePerformanceMetrics(closedPositions) {
  if (closedPositions.length === 0) {
    return {
      totalTrades: 0,
      winRate: 0,
      avgWin: 0,
      avgLoss: 0,
      profitFactor: 0,
      sharpeRatio: 0
    };
  }

  const wins = closedPositions.filter(p => p.result === 'win');
  const losses = closedPositions.filter(p => p.result === 'loss');

  const winRate = wins.length / closedPositions.length;
  const avgWin = wins.length > 0 ? wins.reduce((sum, w) => sum + w.pips, 0) / wins.length : 0;
  const avgLoss = losses.length > 0 ? Math.abs(losses.reduce((sum, l) => sum + l.pips, 0) / losses.length) : 0;

  const grossProfit = wins.reduce((sum, w) => sum + w.pips, 0);
  const grossLoss = Math.abs(losses.reduce((sum, l) => sum + l.pips, 0));
  const profitFactor = grossLoss > 0 ? grossProfit / grossLoss : 0;

  // Simplified Sharpe ratio calculation
  const returns = closedPositions.map(p => p.roi);
  const avgReturn = returns.reduce((sum, r) => sum + r, 0) / returns.length;
  const returnStdDev = Math.sqrt(returns.reduce((sum, r) => sum + Math.pow(r - avgReturn, 2), 0) / returns.length);
  const sharpeRatio = returnStdDev > 0 ? avgReturn / returnStdDev : 0;

  return {
    totalTrades: closedPositions.length,
    winRate: winRate,
    avgWin: avgWin,
    avgLoss: avgLoss,
    profitFactor: profitFactor,
    sharpeRatio: sharpeRatio
  };
}

function checkRetrainingCriteria(metrics) {
  // Trigger retraining if performance degrades
  const minWinRate = 0.55;
  const minProfitFactor = 1.2;
  const minSharpeRatio = 0.5;

  return metrics.winRate < minWinRate || 
         metrics.profitFactor < minProfitFactor || 
         metrics.sharpeRatio < minSharpeRatio;
}

return trackPerformance();

// Advanced News Analysis and Sentiment Processing
const processNews = () => {
  const reutersData = $input.first().json;
  const bloombergData = $input.all()[1]?.json || {};

  const allNews = [];

  // Process Reuters news
  if (reutersData && reutersData.items) {
    reutersData.items.forEach(item => {
      allNews.push({
        title: item.title,
        description: item.description,
        source: 'Reuters',
        timestamp: item.pubDate,
        url: item.link
      });
    });
  }

  // Process Bloomberg news
  if (bloombergData && bloombergData.items) {
    bloombergData.items.forEach(item => {
      allNews.push({
        title: item.title,
        description: item.description,
        source: 'Bloomberg',
        timestamp: item.pubDate,
        url: item.link
      });
    });
  }

  // Analyze each news item
  const analyzedNews = allNews.map(news => {
    const sentiment = analyzeSentiment(news.title + ' ' + news.description);
    const relevance = calculateRelevance(news.title + ' ' + news.description);
    const impact = assessImpact(news, sentiment, relevance);
    const affectedPairs = extractCurrencyPairs(news.title + ' ' + news.description);

    return {
      ...news,
      sentiment: sentiment,
      relevance: relevance,
      impact: impact,
      affectedPairs: affectedPairs,
      processed: true,
      processingTimestamp: new Date().toISOString()
    };
  });

  // Filter high-impact news
  const highImpactNews = analyzedNews.filter(news => 
    news.relevance > 0.6 && Math.abs(news.sentiment) > 0.3
  );

  // Generate market sentiment summary
  const marketSentiment = generateMarketSentiment(analyzedNews);

  return [{
    json: {
      totalNews: analyzedNews.length,
      highImpactNews: highImpactNews,
      marketSentiment: marketSentiment,
      processedAt: new Date().toISOString(),
      newsItems: analyzedNews.slice(0, 20) // Limit to top 20
    }
  }];
};

function analyzeSentiment(text) {
  const positiveWords = [
    'growth', 'increase', 'rise', 'bullish', 'positive', 'strong', 'gain',
    'boost', 'surge', 'rally', 'optimistic', 'confident', 'recovery',
    'expansion', 'improvement', 'success', 'profit', 'earnings'
  ];

  const negativeWords = [
    'decline', 'fall', 'bearish', 'negative', 'weak', 'loss', 'drop',
    'crash', 'crisis', 'recession', 'concern', 'worry', 'fear',
    'uncertainty', 'risk', 'threat', 'problem', 'issue'
  ];

  const words = text.toLowerCase().split(/\s+/);
  let sentiment = 0;

  words.forEach(word => {
    if (positiveWords.includes(word)) sentiment += 1;
    if (negativeWords.includes(word)) sentiment -= 1;
  });

  // Normalize sentiment to -1 to 1 range
  const maxWords = Math.max(positiveWords.length, negativeWords.length);
  return Math.max(-1, Math.min(1, sentiment / maxWords));
}

function calculateRelevance(text) {
  const forexKeywords = [
    'central bank', 'interest rate', 'inflation', 'gdp', 'employment',
    'fed', 'ecb', 'boe', 'boj', 'monetary policy', 'fiscal policy',
    'trade war', 'brexit', 'election', 'currency', 'dollar', 'euro',
    'pound', 'yen', 'forex', 'exchange rate', 'economic data'
  ];

  const text_lower = text.toLowerCase();
  let relevanceScore = 0;

  forexKeywords.forEach(keyword => {
    if (text_lower.includes(keyword)) {
      relevanceScore += 1;
    }
  });

  return Math.min(relevanceScore / forexKeywords.length, 1.0);
}

function assessImpact(news, sentiment, relevance) {
  let impact = relevance * Math.abs(sentiment);

  // Boost impact for certain sources
  if (news.source === 'Reuters' || news.source === 'Bloomberg') {
    impact *= 1.2;
  }

  // Boost impact for recent news
  const newsAge = (new Date() - new Date(news.timestamp)) / (1000 * 60 * 60); // hours
  if (newsAge < 2) {
    impact *= 1.3;
  } else if (newsAge < 6) {
    impact *= 1.1;
  }

  return Math.min(impact, 1.0);
}

function extractCurrencyPairs(text) {
  const currencies = ['USD', 'EUR', 'GBP', 'JPY', 'CHF', 'CAD', 'AUD', 'NZD'];
  const foundCurrencies = [];

  currencies.forEach(currency => {
    if (text.toUpperCase().includes(currency)) {
      foundCurrencies.push(currency);
    }
  });

  const pairs = [];
  for (let i = 0; i < foundCurrencies.length; i++) {
    for (let j = i + 1; j < foundCurrencies.length; j++) {
      pairs.push(foundCurrencies[i] + foundCurrencies[j]);
      pairs.push(foundCurrencies[j] + foundCurrencies[i]);
    }
  }

  return pairs;
}

function generateMarketSentiment(newsItems) {
  if (newsItems.length === 0) {
    return {
      overall: 0,
      confidence: 0,
      trend: 'neutral'
    };
  }

  const weightedSentiment = newsItems.reduce((sum, news) => {
    return sum + (news.sentiment * news.relevance * news.impact);
  }, 0);

  const totalWeight = newsItems.reduce((sum, news) => {
    return sum + (news.relevance * news.impact);
  }, 0);

  const overallSentiment = totalWeight > 0 ? weightedSentiment / totalWeight : 0;
  const confidence = Math.min(totalWeight / newsItems.length, 1.0);

  let trend = 'neutral';
  if (overallSentiment > 0.2) {
    trend = 'bullish';
  } else if (overallSentiment < -0.2) {
    trend = 'bearish';
  }

  return {
    overall: overallSentiment,
    confidence: confidence,
    trend: trend,
    newsCount: newsItems.length
  };
}

return processNews();

